

frappe.views.QueryReport = frappe.views.QueryReport.extend({

    make: function make() {
        this._super();
        this.wrapper.find(".chart-area").after('<div class="pivot-area"></div>');
        this.pivot_area = this.wrapper.find(".pivot-area");
    },

    make_toolbar: function make_toolbar() {
        this._super();
        this.print_icon = this.page.add_action_icon("fa fa-print", function () {
            me.frm.print_doc();
        });
    },

    render: function render(res) {
        this._super(res);
        this.setup_pivot(res);
    },

    setup_pivot: function setup_pivot(res) {
        var opts = res.pivot;
        $.extend(opts, {
            parent: ".pivot-area",
            height: 200
        });

        $(this.pivot_area).css("margin-top", "20px");
        $(this.pivot_area).css("margin-bottom", "20px");

        window.res = res;
        var pivot_columns = [];
        $.each(this.columns, function (i, col) {
            if (i > 0) pivot_columns.push(col.label);
        });
        res.result.unshift(pivot_columns);

        $(this.pivot_area).pivotUI(res.result, { rows: pivot_columns.slice(0, 1), columns: pivot_columns.slice(1) });
        this.pivot_area.toggle(true);
    }

});